  data: () => ({}),
  methods: {
    saveNota(newNota) {
      debugger;
      this.allNotes.push(newNota);
      this.saveFinish = true;
    },

    searchNote(txtSearch) {
      for (let i = 0; i < this.allNotes.length; i++) {
        if (this.allNotes[i].noteTitle.toLowerCase().includes(txtSearch.toLowerCase()) || this.allNotes[i].noteDescription.toLowerCase().includes(txtSearch.toLowerCase()) || this.allNotes[i].priorityLevel.toLowerCase().includes(txtSearch.toLowerCase())) {
          this.allNotes[i].show = true;
        } else {
          this.allNotes[i].show = false;
        }
      }
    }

  },
  components: {
    MenuAside,
    FormNewNote,
    InputSearchNote,
    CardNote
  }
};
