import FormNewNote from "../components/Notes/FormNewNote.vue";
import InputSearchNote from "../components/Notes/InputSearchNote.vue";
import CardNote from "../components/Notes/CardNote.vue";
import MenuAside from "../components/MenuAside.vue";
export default {
  name: "Home",
  data: () => ({
    newNote: ''
  }),
  methods: {
    saveNota(newNota) {
      this.newNote = newNota;
    },

    searchNote(txtSearch) {
      for (let i = 0; i < this.allNotes.length; i++) {
        if (this.allNotes[i].noteTitle.toLowerCase().includes(txtSearch.toLowerCase()) || this.allNotes[i].noteDescription.toLowerCase().includes(txtSearch.toLowerCase()) || this.allNotes[i].priorityLevel.toLowerCase().includes(txtSearch.toLowerCase())) {
          this.allNotes[i].show = true;
        } else {
          this.allNotes[i].show = false;
        }
      }
    }

  },
  components: {
    MenuAside,
    FormNewNote,
    InputSearchNote,
    CardNote
  }
};
